customCSS:
*{
    font-family: 'Lato';
}
.siteforcePrmBody .cCenterPanel,
.cCenterPanel {
	padding: 0;
	margin: 0;
}

 #LoginNew .cHeaderPanel,  #LoginNew .cFooterPanel{
    position:absolute;
}
.siteforcePrmBody .cCenterPanel .siteforceContentArea .contentRegion {
	padding: 0;
}
.siteforcePrmBody .cCenterPanel .siteforceContentArea .ui-widget .forceCommunitySection .cb-section_row .cb-section_column,
.slds-col--padded {
	padding: 0!important;
}
html,
body,
.siteforcePrmBody,
.siteforceContentArea,
.contentRegion,
[data-region-name="content"],
.ui-widget,
iframe,
.cCenterPanel,
.forceCommunitySection,
cb-section_row,
.siteforceStarterBody {
	height: 100%;
    font-family: Lato;
}
#LoginNew{

    background-color:white;
}
#LoginNew .cCenterPanel {
	width: 100%;
	height: 100%;
	max-width: 32rem !important;
    z-index:9;
}
.cHeaderPanel{
    background-color:#0079c1;
    height:4rem;
}
.comm-page-login-home .cHeaderPanel [data-region-name="header"], .comm-page-forgot-password .cHeaderPanel [data-region-name="header"], .comm-page-check-password .cHeaderPanel [data-region-name="header"] {
	height: 100%;
}

.comm-page-login-home .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock, .comm-page-forgot-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock, .comm-page-check-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock {
	height: 100%;
	margin: 0;
}

.comm-page-login-home .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"], .comm-page-forgot-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"], .comm-page-check-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] {
	height: 100%;
	display: flex;
	align-items: center;
	justify-content: start;
}

.comm-page-login-home .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img, .comm-page-forgot-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img, .comm-page-check-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img {
	height: 2.5rem;
	width: 9rem;
}

.cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header{
	padding-left:1rem;
}
.comm-page-login-home .cCenterPanel .siteforceContentArea {
	width: 80%;
	margin: 0 auto;
}

@media (max-width: 420px) {
	.comm-page-login-home .cCenterPanel .siteforceContentArea {
		width: 100%;
		margin: unset;
	}
}

.comm-page-login-home .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-title {
	text-align: center;
	font-size: 1.6rem;
}

@media (max-width: 420px) {
	.comm-page-login-home .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-title {
		font-size: 1.3rem;
	}
}

.comm-page-login-home .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-subtitle {
	position: unset;
	margin-top: 1rem;
	text-align: center;
	padding: 0 1.3rem;
}

@media (max-width: 420px) {
	.comm-page-login-home .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-subtitle {
		padding: 0 0;
	}
}

.comm-page-login-home .cCenterPanel .siteforceContentArea .ui-widget [id="cls_login_container"] [id="sfdc_forgot"] {
	display: flex;
	justify-content: center;
}

.comm-page-login-home .cCenterPanel .siteforceContentArea [data-region-name="content"] {
	margin-top: 1rem;
}

@media (max-width: 420px) {
	.comm-page-login-home .cCenterPanel .siteforceContentArea [data-region-name="content"] {
		margin-top: 3rem;
	}
}
.comm-page-forgot-password .cCenterPanel .siteforceContentArea {
	width: 80%;
	margin: 0 auto;
}

@media (max-width: 420px) {
	.comm-page-forgot-password .cCenterPanel .siteforceContentArea {
		width: 100%;
		margin: unset;
	}
}

.comm-page-forgot-password .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-title {
	text-align: center;
	font-size: 1.4rem;
	padding:0 2rem;
}

@media (max-width: 420px) {
	.comm-page-forgot-password .cCenterPanel .siteforceContentArea .ui-widget [dir="ltr"] .container .container-title {
		font-size: 1.3rem;
		padding:0rem;
	}
}
@media (max-width: 420px) {
	.cCenterPanel .ui-widget .cls_lightning {
		padding: 1.4rem 0 !important;
	}
}
@media only screen and (max-width: 550px) {
  #LoginNew .cCenterPanel {
    max-width: initial !important;
    width: calc(100% - 2rem);
  }
}

#LoginNew .cCenterPanel #cls_login_container .input {
	padding: 15px 15px 15px 35px;
}
#LoginNew .cls_lightning #sfdc_forgot a {
  color: #2574a9;
}
.cHeaderPanel::before, .cCenterPanel::before{
    display: none;
}

#LoginNew .cCenterPanel .document-container {
	 display: flex;
	 flex-direction: column;
	 align-items: center;
}
 #LoginNew .cCenterPanel .document-container .header {
	 text-transform: uppercase;
	 font-size: 1.4rem;
	 display: flex;
	 justify-content: center;
	 margin-bottom: 1rem;
	 text-align: center;
}
 @media (max-width: 520px) {
	 #LoginNew .cCenterPanel .document-container .header {
		 font-size: 1.2rem;
	}
}
 #LoginNew .cCenterPanel .document-container a {
	 width: fit-content;
	 text-align: center;
	 margin-top: 2rem;
	 padding: 0.5rem 2rem;
	 background-color: #0079c1;
	 color: white;
	 border-radius: 2px;
	 text-transform: uppercase;
	 text-decoration:none;
}


.document-container{
    background-color: white;
    padding: 1.4rem 1.5rem 6rem !important;
    border-radius: 0.5rem;
}

.document-container .body{
    text-align: center;
}

.ui-widget{
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

.comm-page-login-home .cCenterPanel .siteforceContentArea [data-region-name="content"] .ui-widget .communityLogoContainer .uiImage > img, .comm-page-forgot-password .cCenterPanel .siteforceContentArea [data-region-name="content"] .ui-widget .communityLogoContainer .uiImage > img {
	height: 3.5rem;
	width: 12rem !important;
}

@media (max-width: 420px) {
	.comm-page-login-home .cCenterPanel .siteforceContentArea [data-region-name="content"] .ui-widget .communityLogoContainer .uiImage > img, .comm-page-forgot-password .cCenterPanel .siteforceContentArea [data-region-name="content"] .ui-widget .communityLogoContainer .uiImage > img {
		height: 2.5rem;
		width: 9rem !important;
	}
}
.comm-page-login-home .cCenterPanel .siteforceContentArea [data-region-name="content"] button span, .comm-page-forgot-password .cCenterPanel .siteforceContentArea [data-region-name="content"] button span {
	font-weight: 400;
}

[data-region-name="content"] .ui-widget .sfdc button{
    border-radius: 0.3rem;
}

.forceCommunityRichText{
   margin: 1rem 0 1rem;
}

.forceCommunityRichText .uiOutputRichText {
    display: flex;
    justify-content: center;
}

.forceCommunityRichText .uiOutputRichText p{
    bottom: 5rem;
    position: absolute;
}

.forceCommunityRichText .uiOutputRichText p a{
	text-decoration: underline;    
}

.cls_lightning{
    background-color: white;
    padding: 1.4rem 1.5rem !important;
    border-radius: 0.5rem;
}

.forceCommunityRichText .uiOutputRichText .container-title{
    font-size: 1.3rem;
}

.siteforceContentArea [data-region-name="content"] >.ui-widget:nth-child(3) .cls_lightning a{
    color: #666666;
    font-weight: 500;
}

.siteforceContentArea [data-region-name="content"] >.ui-widget:nth-child(3) button{
    border-color: #0070d2;
}

.siteforceContentArea [data-region-name="content"] >.ui-widget:nth-child(3) input{
    padding: 0.8rem 2.5rem 0.85rem !important;
    background-color: #1970d212;
    border: none !important;
}

#error{
    padding: 0 0 1rem 0;
}
.PHONE,
.DESKTOP,
.TABLET {
	display: none;
}


.cHeaderPanel {
    background-color: #1e5632 !important;

}

div[id="cls_self_register_container"] button.sfdc_button, div[id="cls_forgotpassword_container"] button.sfdc_button, div[id="cls_resetpassword_container"] button.sfdc_button, div[id="cls_login_container"] button.sfdc_button{
    background-color: #1e5632 !important;
    border-color: #1e5632 !important;
}

#LoginNew .cls_lightning #sfdc_forgot a{
    color:#1e5632 !important;
}

.cHeaderPanel {
    height: 80px !important;
}

.comm-page-login-home .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img, .comm-page-forgot-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img, .comm-page-check-password .cHeaderPanel [data-region-name="header"] .forceCommunityHtmlBlock [dir="ltr"] .cobalt-header img {
    height: 3rem !important;
    width: 11.5rem !important;
}
